package org.cat.eye.performance.test.app;

import org.cat.eye.performance.test.list.LinkedList;

import static org.cat.eye.performance.test.utilities.StringUtils.join;
import static org.cat.eye.performance.test.utilities.StringUtils.split;
import static org.cat.eye.performance.test.app.MessageUtils.getMessage;

import org.apache.commons.text.WordUtils;

public class App {
    public static void main(String[] args) {
        LinkedList tokens;
        tokens = split(getMessage());
        String result = join(tokens);
        System.out.println(WordUtils.capitalize(result));
    }
}
